import {Reduce} from "./reduce";

/**
 * Clase AddReduce
 */
export class AddReduce extends Reduce {
  /**
   * Constructor de la clase AddReduce que hereda de Reduce
   * @param numbers Array de números a operar
   */
  protected result: number;
  constructor(protected numbers: number[]) {
    super();
    this.result = 0;
  }

  /**
   * Método que inicializa la colección de datos
   */
  protected initCollection(): void {
    this.collection = this.numbers;
  }

  /**
   * Método que realiza la operación de suma de la coleccion de datos
   */
  protected operacion() {
    this.numbers.forEach((element) => {
      this.result += element;
    });
  }

  /**
   * Metodo que imprime el resultado de la operacion reduce
   */
  protected print(): void {
    console.log(`El resultado de AddReduce de ${this.collection} = ${this.result}`);
  }
}

/**
 * Clase abstracta Reduce
 */
export abstract class Reduce {
  /**
   * Constructor de la clase Reduce
   * @param collection arrat de números a operar
   */
  protected collection: number[];
  constructor() {
    this.collection = [];
  }

  /**
   * Método que establece los pasos y operaciones comunes
   */
  public run() {
    // Se inicializa la colección
    this.initCollection();

    // Hook
    this.afterInitialisation();

    // Se realiza la operación
    this.operacion();

    // Hook
    this.afterOperacion();

    // Se imprime el resultado
    this.print();

    // Hook
    this.afterPrint();
  }

  /**
   * Método abstracto que se debe definir en las clases hijas y que inicializa la coleccion de datos
   */
  protected abstract initCollection(): void;

  /**
   * Método abstracto que se debe definir en las clases hijas y que opera sobre la coleccion de datos
   */
  protected abstract operacion(): void;

  /**
   * Método abstracto que se debe definir en las clases hijas y que imprime el resultado
   */
  protected abstract print(): void;

  /**
   * Método hook que representan pasos u operaciones opcionales
   */
  protected afterInitialisation() {}

  /**
   * Método hook que representan pasos u operaciones opcionales
   */
  protected afterOperacion() {}

  /**
   * Método hook que representan pasos u operaciones opcionales
   */
  protected afterPrint() {}
}

import {Reduce} from "./reduce";

/**
 * Clase SubReduce
 */
export class SubReduce extends Reduce {
  /**
   * Constructor de la clase SubReduce que hereda de Reduce
   * @param numbers Array de números a operar
   */
  protected result: number;
  constructor(protected numbers: number[]) {
    super();
    this.result = 0;
  }

  /**
   * Método que inicializa la colección de datos
   */
  protected initCollection(): void {
    this.collection = this.numbers;
  }

  /**
   * Método que realiza la operación de resta de la coleccion de datos
   */
  protected operacion() {
    this.numbers.forEach((element) => {
      this.result -= element;
    });
  }

  /**
   * Metodo que imprime el resultado de la operacion reduce
   */
  protected print(): void {
    console.log(`El resultado de SubReduce de ${this.collection} = ${this.result}`);
  }
}

/**
 * Aplicación para el procesamiento de notas de texto
 * @module Nota
 * @author Andrea Hernández Martín
 */

import * as chalk from 'chalk';

/**
 * Clase Nota
 */
export class Nota {
  /**
   * Constructor de la clase Nota
   * @param titulo Nombre de la nota
   * @param cuerpo Texto escrito en la nota
   * @param color Color del texto de la nota
   */
  constructor(private titulo: string, private cuerpo: string, private color: string) {}

  /**
   * Método getter que accede al título de la nota
   * @returns Retorna el titulo de la nota
   */
  getTitulo(): string {
    return this.titulo;
  }

  /**
   * Método getter que accede al cuerpo de la nota
   * @returns Retorna el cuerpo de la nota
   */
  getCuerpo(): string {
    return this.cuerpo;
  }

  /**
   * Método getter que accede al color de la nota
   * @returns Retorna el color de la nota
   */
  getColor(): string {
    return this.color;
  }

  /**
   * Método getter que accede a los datos de la nota completa
   * @returns Retorna la nota entera
   */
  getNota(): string {
    const nota: string = `"Titulo": "${this.titulo}",\n"Cuerpo": "${this.cuerpo}",\n"Color": "${this.color}"`;
    return nota;
  }

  /**
   * Método que imprime la nota según el color elegido
   */
  print(): void {
    switch (this.color) {
      case 'Rojo':
        console.log(chalk.default.red(this.getNota()));
        break;
      case 'Verde':
        console.log(chalk.default.green(this.getNota()));
        break;
      case 'Azul':
        console.log(chalk.default.blue(this.getNota()));
        break;
      case 'Amarillo':
        console.log(chalk.default.yellow(this.getNota()));
        break;
      case 'Magenta':
        console.log(chalk.default.magenta(this.getNota()));
        break;
      case 'Cian':
        console.log(chalk.default.cyan(this.getNota()));
        break;
      default:
        console.log(chalk.default.red('Error. Este color no está disponible'));
        break;
    }
  }
}

/**
 * Aplicación para el procesamiento de notas de texto
 * @module Lista
 * @author Andrea Hernández Martín
 */

import * as chalk from "chalk";
import {spawn} from "child_process";
import {existsSync, readdirSync, readFile, writeFile} from "fs";
import {Nota} from "./nota";

/**
 * Clase Lista
 */
export class Lista {
  /**
   * Constructor de la clase Lista
   * @param userName nombre del usurio
   */
  constructor(private userName: string) {}

  /**
   * Método que accede al nombre del usuario
   * @returns Retorna el nombre del usuario
   */
  getUserName(): string {
    return this.userName;
  }

  /**
   * Método que busca una nota según el nombre de esta
   * @param nombre Nombre de la nota a buscar
   * @returns Retorna verdadero si la encontró y falso en caso contrario
   */
  findNota(nombre: string): boolean {
    return (existsSync(`./${this.userName}/${nombre}.json`)) ? true : false;
  }

  /**
   * Método que añade una nueva nota
   * @param nuevaNota Nota nueva que se quiere añadir
   */
  addNota(nuevaNota: Nota): void {
    if (!this.findNota(nuevaNota.getTitulo())) {
      writeFile(`./${this.userName}/${nuevaNota.getTitulo()}.json`, `{\n${nuevaNota.getNota()}\n}`, (err) => {
        if (!err) {
          console.log(chalk.default.green('Se ha añadido la nota correctamente'));
        }
      });
    } else {
      console.log(chalk.default.red('Error. Ya existe una nota con ese nombre'));
    }
  }

  /**
   * Método que modifica el contenido de una nota existente
   * @param nombre Nombre de la nota que se quiere modificar
   * @param nuevoTexto El texto a modificar
   * @param nuevoColor El color de la nota que se quiere modificar
   */
  modifyNota(nombre: string, nuevoTexto: string, nuevoColor: string): void {
    if (this.findNota(nombre)) {
      writeFile(`./${this.userName}/${nombre}.json`, `{\n"Titulo": "${nombre}",\n"Cuerpo": "${nuevoTexto}",\n"Color": "${nuevoColor}"\n}`, (err) => {
        if (!err) {
          console.log(chalk.default.green('Se ha modificado la nota correctamente'));
        }
      });
    } else {
      console.log(chalk.default.red('Error. No existe ninguna nota con ese nombre'));
    }
  }

  /**
   * Método que elimina una nota
   * @param nombre Nombre de la nota que se quiere eliminar
   */
  deleteNota(nombre: string): void {
    if (this.findNota(nombre)) {
      spawn('rm', [`./${this.userName}/${nombre}.json`]);
      console.log(chalk.default.green('Se ha eliminado la nota correctamente'));
    } else {
      console.log(chalk.default.red('Error. No existe ninguna nota con ese nombre'));
    }
  }

  /**
   * Método que muestra por consola todas las notas que tiene un usuario
   * con su color correspondiente
   */
  listarTitulos(): void {
    if (existsSync(`./${this.userName}`)) {
      const lista = readdirSync(`./${this.userName}`);
      lista.forEach((nota) => {
        readFile(`./${this.userName}/${nota}`, (err, data) => {
          if (!err) {
            const dataJson = JSON.parse(data.toString());
            switch (dataJson.Color) {
              case 'Rojo':
                console.log(chalk.default.red(dataJson.Titulo));
                break;
              case 'Verde':
                console.log(chalk.default.green(dataJson.Titulo));
                break;
              case 'Azul':
                console.log(chalk.default.blue(dataJson.Titulo));
                break;
              case 'Amarillo':
                console.log(chalk.default.yellow(dataJson.Titulo));
                break;
              case 'Magenta':
                console.log(chalk.default.magenta(dataJson.Titulo));
                break;
              case 'Cian':
                console.log(chalk.default.cyan(dataJson.Titulo));
                break;
              default:
                console.log(chalk.default.red('Error. Este color no está disponible'));
                break;
            }
          }
        });
      });
    } else {
      console.log(chalk.default.red('Error. No existen listas para este usuario'));
    }
  }

  /**
   * Método que lee una nota según el nombre que se le pase
   * @param nombre Nombre de la nota que se quiere leer
   */
  leerNota(nombre: string): void {
    if (this.findNota(nombre)) {
      readFile(`./${this.userName}/${nombre}.json`, (err, data) => {
        if (!err) {
          const dataJson = JSON.parse(data.toString());
          switch (dataJson.Color) {
            case 'Rojo':
              console.log(chalk.default.red(`Titulo: ${dataJson.Titulo}\nCuerpo: ${dataJson.Cuerpo}`));
              break;
            case 'Verde':
              console.log(chalk.default.green(`Titulo: ${dataJson.Titulo}\nCuerpo: ${dataJson.Cuerpo}`));
              break;
            case 'Azul':
              console.log(chalk.default.blue(`Titulo: ${dataJson.Titulo}\nCuerpo: ${dataJson.Cuerpo}`));
              break;
            case 'Amarillo':
              console.log(chalk.default.yellow(`Titulo: ${dataJson.Titulo}\nCuerpo: ${dataJson.Cuerpo}`));
              break;
            case 'Magenta':
              console.log(chalk.default.magenta(`Titulo: ${dataJson.Titulo}\nCuerpo: ${dataJson.Cuerpo}`));
              break;
            case 'Cian':
              console.log(chalk.default.cyan(`Titulo: ${dataJson.Titulo}\nCuerpo: ${dataJson.Cuerpo}`));
              break;
            default:
              console.log(chalk.default.red('Error. Este color no está disponible'));
              break;
          }
        } else {
          console.log(chalk.default.red('Error. No se pudo leer la nota deseada'));
        }
      });
    } else {
      console.log(chalk.default.red('Error. No existe una nota con ese nombre'));
    }
  }
}
